onOptionChange:
{{
//filtros.filtrarEAtualizarAlertas()
(function() {
    if(seletor_animal.selectedOptionValue === -1) {
			//pegar todas Vacinas
			medicamentosFiltrados.getTodosMedicamentosAnimal();
			getTodosAlertaVacina.run().then(() => {
		alertas_vacina.setData(getTodosAlertaVacina.data.data);
			});
			//pegar todas Coleiras
			getTodosAlertaColeira.run().then(() => {			alertas_coleira.setData(getTodosAlertaColeira.data.data);
			});	
			//pegar todos Vermifugos
			getTodosVermifugos.run().then(() => {			alertas_vermifugo.setData(getTodosVermifugos.data.data);
			});	
			//pegar todos Ectoparasitas
			getTodosEctoparasiticida.run().then(() => {			alertas_ectoparasitisida.setData(getTodosEctoparasiticida.data.data);
			});	

    } else {
			medicamentosFiltrados.getUmMedicamentosAnimal();
			//pegar as Vacinas de um Cao
			getAlertaVacina.run().then(() => {
				alertas_vacina.setData(getAlertaVacina.data.data);
			});
			//pegar a Coleira de um Cao
			getAlertaColeiraUmCao.run().then(() => {
				alertas_coleira.setData(getAlertaColeiraUmCao.data.data);
			});
			//pegar os Vermifugos de um Cao
			get_vermifugosDeCao.run().then(() => {
				alertas_vermifugo.setData(get_vermifugosDeCao.data.data);
			});
			//pegar os Ectoparasiticidas de um Cao
			get_ectoparasiticidaDeCao.run().then(() => {
				alertas_ectoparasitisida.setData(get_ectoparasiticidaDeCao.data.data);
			});
    }
})();
}}
