import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
import { Button, Card } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm'
import Markdown from 'https://cdn.jsdelivr.net/npm/react-markdown@9.0.1/+esm';

function App() {
    const handleMyButton = () => {
        appsmith.triggerEvent('onMyButtonClick');
    };

    return (
        <Card className="app">
            <div className="tip-container">
                <div className="tip-header">
                    <h2>Custom Widget</h2>
                </div>
                {/* Aqui você pode adicionar a lógica para exibir a dica */}
            </div>
            <div className="button-container">
                {/* Novo botão adicionado */}
                <Button className="myButton" onClick={handleMyButton}>My Button</Button>
            </div>
        </Card>
    );
}

appsmith.onReady(() => {
    reactDom.render(<App />, document.getElementById("root"));
});
