Receita {{
(function() {
	const data_ini = currentItem.data_inicio;
    // Divide a data pelo caractere '-'
  const partes_inicio = data_ini.split('-');
    // Rearranja as partes no formato dia/mes/ano
  const data_ini_formatada = `${partes_inicio[2]}/${partes_inicio[1]}/${partes_inicio[0]}`;
	return data_ini_formatada;
})()
}} - {{(function() {
	const data_fim = currentItem.data_fim;
    // Divide a data pelo caractere '-'
  const partes_fim = data_fim.split('-');
    // Rearranja as partes no formato dia/mes/ano
  const data_fim_formatada = `${partes_fim[2]}/${partes_fim[1]}/${partes_fim[0]}`;
	return data_fim_formatada;
})()
}}
